<script>
    jQuery(document).on('ready', function () {
         // Открыть модальное окно опросника
        document.getElementById("open-modal-btn-zakaz").addEventListener("click", function() {
            document.getElementById("zakaz").classList.add("open")
        })

        // Закрыть модальное окно опросника
        document.getElementById("close-zakaz").addEventListener("click", function () {
            document.getElementById("zakaz").classList.remove("open")
        })

        window.addEventListener('keydown', (e) => {
            if (e.key === "Escape") {
                document.getElementById("zakaz").classList.remove("open")
            }
        });
        document.querySelector("#zakaz .modal__box").addEventListener('click', event => {
            event._isClickWithInModal = true;
        });
        document.getElementById("zakaz").addEventListener('click', event => {
            if (event._isClickWithInModal) return;
            event.currentTarget.classList.remove('open');
        });

        $("#files").change(function() {
            filename = this.files[0].name;
            console.log(filename);
            $('.file_form').html('');
            $('.file_form').append('<i class=\"fa fa-paperclip\" aria-hidden=\"true\"></i>' + filename);
        });
    })
</script>


